import React, { useEffect, useState } from 'react'
import styled from 'styled-components'
function EmailComponent() {
    return (
        <>
            <div>
                <label for="email">Please Enter Your Emails</label>
            </div>
            <div>
                <textarea id="emails" name="email" rows="10" cols="101">
                </textarea>
            </div>

        </>
    )
}

const Wrapper = styled.div`
  color: #ffffff;
  padding: 16px;
  font-size: 14px;
  line-height: 1.5;
  display: flex;
  align-items: center;
  justify-content: center;

textarea {
    font - size: 0.8rem;
    letter - spacing: 1px;
}
  
  textarea {
    padding: 10px;
    max - width: 100 %;
    line - height: 1.5;
    border - radius: 5px;
    border: 1px solid #ccc;
    box - shadow: 1px 1px 1px #999;
}
  
  label {
    display: block;
    margin - bottom: 10px;
}
`;
export default EmailComponent
